Channel.isAddress = function (address) {
      if (!/^(0x)?[0-9a-f]{40}$/i.test(address)) {
      return false;
  } else if (/^(0x)?[0-9a-f]{40}$/.test(address) || /^(0x)?[0-9A-F]{40}$/.test(address)) {
      return true;
  } else {
      return Contract.isChecksumAddress(address);
  }
};


